        "Image" bytea NULL,
        "CreationDate" timestamp with time zone NOT NULL DEFAULT (CURRENT_DATE),
        "ModificationDate" timestamp with time zone NOT NULL DEFAULT (CURRENT_DATE),
        CONSTRAINT "PK_Product" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
