DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20230708184818_Initial') THEN
    PERFORM setval(
        pg_get_serial_sequence('"Users"', 'Id'),
        GREATEST(
            (SELECT MAX("Id") FROM "Users") + 1,
            nextval(pg_get_serial_sequence('"Users"', 'Id'))),
        false);
    PERFORM setval(
        pg_get_serial_sequence('"UserInfo"', 'Id'),
        GREATEST(
            (SELECT MAX("Id") FROM "UserInfo") + 1,
            nextval(pg_get_serial_sequence('"UserInfo"', 'Id'))),
        false);
    PERFORM setval(
        pg_get_serial_sequence('"UserRole"', 'Id'),
        GREATEST(
            (SELECT MAX("Id") FROM "UserRole") + 1,
            nextval(pg_get_serial_sequence('"UserRole"', 'Id'))),
        false);
    END IF;
END $EF$;

